                new Product { Id = 2, Brand = "HP (Hewlett-Packard)", Name = "EliteBook ", Price = 2000, Description = "Versatile 2‑in‑1 convertibles (Spectre, Envy), professional business machines (Elite), and gaming rigs (Omen). Known for elegant designs and solid performance", ImageLink = "data:image/jpeg;base64," },
                new Product { Id = 3, Brand = "Apple (Macintosh)", Name = "MacBook Pro", Price = 1599, Description = "Premium aluminum build, industry-leading performance with M4 chips, outstanding battery life, and tight hardware/software integration—ideal for creatives and professionals", ImageLink = "" },
                new Product { Id = 4, Brand = "Dell", Name = "XPS", Price = 1149, Description = "Flagship XPS series with sleek aluminum and Gorilla Glass, powerful Intel Ultra chips, OLED displays, and strong build quality—excellent for productivity and light content creation.", ImageLink = "data:image/jpeg;base64," },
                new Product { Id = 5, Brand = "Asus", Name = "ROG", Price = 2500, Description = "Offers value-packed ultrabooks (ZenBook, VivoBook), high-end creative/gaming rigs (ProArt, ROG) with strong performance, sleek design, and innovative display options.", ImageLink = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRS3T7Evt3oOwV54HcMYB0JydWfyQVP1-TQOg&s" },
