                new Product { Id = 9, Brand = "Samsung", Name = "Galaxy Book", Price = 1599, Description = "Lightweight Windows laptops often with AMOLED screens, stylus-ready models—designed for mobile productivity closely integrated with Samsung ecosystem.", ImageLink = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRvhV98rGYQjSpJB_731lw33LQyA1F6cWrcRA&s" },
